var open_phone=false;$("#phone_number").bind("click",function(){if(!open_phone){$(".phone.close").removeClass("close").addClass("open");open_phone=true;$("#phone_number > i").removeClass("fa-angle-down").addClass("fa-angle-up")}else{$(".phone.open").removeClass("open").addClass("close");open_phone=false;$("#phone_number > i").removeClass("fa-angle-up").addClass("fa-angle-down")}});